import axios from "axios";
import { useState, useEffect } from "react";
import React from "react";
import moment from "moment";
import {
  Center,
  Container,
  Heading,
  Box,
  Text,
  Input,
  Button,
  Flex,
  Image,
  HStack,
  Avatar,
  Stack,
} from "@chakra-ui/react";
import { Link } from "react-router-dom";

const Posts = ({posts}) => {
  const [blog, setBlog] = useState([]);
  const api = "https://foodielandnod.herokuapp.com/api/getAllBlog";

  useEffect(() => {
    window.scroll(0, 0);
    const loadPost = async () => {
      const response = await axios.get(api);
      setBlog(response.data);
    };

    loadPost();
  }, []);

  // const [recipe, setRecipe] = useState([]);
  // const apiRecipe = "https://foodielandnod.herokuapp.com/api/v1/getAllRecipes";
  // useEffect(() => {
  //   const loadPost = async () => {
  //     const response = await axios.get(apiRecipe);
  //     setRecipe(response.data);
  //   };

  //   loadPost();
  // }, []);
  return (
    <Container maxW={1080} mx={"auto"}>
      <Box>
        <Center>
          <Box mt={10}>
            <Heading fontSize={"5xl"}>Blog & Article</Heading>
          </Box>
        </Center>
        <Center>
          <Text mt={5} color={"gray.600"} fontSize={"sm"}>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry.
          </Text>
        </Center>
        <Center>
          <Box mt={12}>
            <Input
              placeholder="Search article, news or receipe..."
              size="lg"
              htmlSize={30}
              width="auto"
              borderRadius={15}
            />
            <Button
              borderRadius={15}
              bgColor={"#000"}
              color={"#fff"}
              size={"lg"}
              ml={2}
            >
              Search
            </Button>
          </Box>
        </Center>
      </Box>
      <Flex mt={20}>
        <Box w={750}>
          {posts.slice(0, 5).map((item) => {
            return (
              <>
                <Flex mb={5}>
                  <Box w={250}>
                    {/* <Img
                    src="images/food-16.png"
                    w={220}
                    h={200}
                    borderRadius={20}
                  /> */}
                    <Link to={`/blogPost/${item._id}`}>
                      <Image
                        w="300px"
                        height="200px"
                        src={
                          "https://foodielandnod.herokuapp.com/" + item.image
                        }
                      />
                    </Link>
                  </Box>
                  <Box w={400}>
                    <Heading fontSize={"md"}>{item.title}</Heading>
                    <Text
                      fontSize={"sm"}
                      color={"gray.500"}
                      textAlign={"justify"}
                    >
                      {item.description}
                    </Text>
                    <HStack mt={35}>
                      <Avatar
                        name="Ryan Florence"
                        src={
                          "https://foodielandnod.herokuapp.com/" + item.Image
                        }
                        size="sm"
                      />
                      <Heading fontSize={"sm"} fontWeight={800}>
                        <Text>
                          {" "}
                          {item.userId.firstName} {item.userId.lastName}
                        </Text>
                      </Heading>
                      <Text fontSize={"sm"}>
                        {moment(item.userId.updatedAt).format("MMM Do YY")}
                      </Text>
                    </HStack>
                  </Box>
                </Flex>
              </>
            );
          })}
        </Box>
        <Box ml={10}>
          <Heading fontSize={"2xl"}>Tasty Receipes</Heading>
          <Flex>
            <Box>
              {blog.slice(2, 5).map((item) => {
                return (
                  <>
                    <HStack mt={5}>
                      <Link to={`/blogPost/${item._id}`}>
                        <Image
                          w="300px"
                          height="200px"
                          src={
                            "https://foodielandnod.herokuapp.com/" + item.image
                          }
                        />
                      </Link>
                      <Stack>
                        <Heading fontSize={"md"}>{item.title}</Heading>
                        <Heading fontSize={"sm"} color={"gray.400"}>
                          <Text>
                            {item.userId.firstName} {item.userId.lastName}
                          </Text>
                        </Heading>
                      </Stack>
                    </HStack>
                  </>
                );
              })}
            </Box>
          </Flex>
        </Box>
      </Flex>
    </Container>
  );
};

export default Posts;
